import React, { useState } from 'react';
import { createBook, updateBook } from '../services/api';

const BookForm = ({ book, onSave }) => {
  const [title, setTitle] = useState(book ? book.title : '');
  const [description, setDescription] = useState(book ? book.description : '');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const bookData = { title, description };
    if (book) {
      await updateBook(book.id, bookData);
    } else {
      await createBook(bookData);
    }
    onSave();
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Title</label>
        <input value={title} onChange={(e) => setTitle(e.target.value)} />
      </div>
      <div>
        <label>Description</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
      </div>
      <button type="submit">Save</button>
    </form>
  );
};

export default BookForm;
