import React, { useState } from 'react';
import { createBorrower, updateBorrower } from '../services/api';

const BorrowerForm = ({ borrower, onSave }) => {
  const [name, setName] = useState(borrower ? borrower.name : '');
  const [email, setEmail] = useState(borrower ? borrower.email : '');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const borrowerData = { name, email };
    if (borrower) {
      await updateBorrower(borrower.id, borrowerData);
    } else {
      await createBorrower(borrowerData);
    }
    onSave();
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name</label>
        <input value={name} onChange={(e) => setName(e.target.value)} />
      </div>
      <div>
        <label>Email</label>
        <input value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <button type="submit">Save</button>
    </form>
  );
};

export default BorrowerForm;
